const cheerio = require('cheerio');
const request = require('request');

request({
    method: 'GET',
    url: 'http://www.hispanicallyspeakingnews.com/' 
}, (err, res, body) => {

    if (err) return console.error(err);

    let $ = cheerio.load(body);

    let title = $('title');

    console.log(title.text());
});



